mapper.select:
SELECT award_id, award_name, award_desc, award_img, point, priority, create_time, expire_time, last_edit_time, enable_status, shop_id FROM tb_award
and award_name like '%${awardCondition.awardName}%'
and shop_id = #{awardCondition.shopId}
and expire_time > #{awardCondition.expireTime}
and enable_status = #{awardCondition.enableStatus}
ORDER BY priority DESC LIMIT #{rowIndex},#{pageSize};

SELECT count(1) FROM tb_award



		and award_name like '%${awardCondition.awardName}%'


		and shop_id = #{awardCondition.shopId}


		and expire_time > #{awardCondition.expireTime}


		and enable_status = #{awardCondition.enableStatus}

mapper.update:
UPDATE tb_award
award_name=#{awardName}, award_desc=#{awardDesc}, award_img=#{awardImg}, point=#{point}, priority=#{priority}, expire_time=#{expireTime}, last_edit_time=#{lastEditTime}, enable_status=#{enableStatus}
WHERE award_id=#{awardId} AND shop_id=#{shopId}

mapper.delete:
DELETE FROM
tb_award
WHERE
award_id = #{awardId}
AND
shop_id=#{shopId}
